import { PayloadAction, createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios, { AxiosResponse, AxiosError } from "axios";
import { tenmien } from "../../../utils";

interface Product {
  product_id: string;
  thumbnail: string;
  name: string;
  reg_price: number;
  discount_price: number;
}

interface CartItem {
  cartItem: number;
  quantity: number;
  total: number;
  saving: number;
  products: Product;
}

interface Cart {
  cart_id: string;
  quantity: number;
  total: number;
  saved: number;
  cartItems: CartItem[];
}

interface cartState {
  loading: boolean;
  error: string;
  data: Cart;
}

// Thunk functions
export const fetchCart = createAsyncThunk("cartSlice/fetchCart", async () => {
  try {
    const response = await axios.get(tenmien + "/cart/");
    return response.data;
  } catch (err) {
    throw err;
  }
});

export const addToCart = createAsyncThunk(
  "cartSlice/addToCart",
  async ({ productId, quantity }: { productId: string; quantity: number }) => {
    try {
      const response = await axios.post(tenmien + "/cart", {
        products: [
          {
            productId,
            quantity,
          },
        ],
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }
);

export const updateQuantityCart = createAsyncThunk(
  "cartSlice/updateQuantityCart",
  async ({
    cartItemId,
    quantity,
  }: {
    cartItemId: string;
    quantity: number;
  }) => {
    try {
      const response = await axios.patch(tenmien + "/cart/" + cartItemId, {
        quantity,
      });
      return response.data;
    } catch (err) {
      throw err;
    }
  }
);

